mapper.select:
		select order_id orderId,order_num orderNum,sub_total subTotal,state,province,city,address,receiver,o.create_time createTime 
		from orders o left join address a on o.address_id=a.id where o.deleted=false
		and seller_id=#{sellerId}
		
			and state=#{status}
		
		
  		and order_num like concat('%',#{keyword},'%')
  		
		order by o.create_time desc
		
  		limit #{offset}, #{pageSize}

  	select count(*) from orders where deleted=0
  	
  	and seller_id=#{sellerId}
  	
  	
			and state=#{status}
		
		
  		and order_num like concat('%',#{keyword},'%')

	select count(*) from orders where deleted=0
	
        and seller_id = #{sellerId}
      
	
       and  create_time >= #{beginDate}
      
	
       and  create_time <= #{endDate}

select sum(sub_total) from orders  where deleted=0 

	and seller_id = #{sellerId}


	and create_time >=
	#{beginDate}


	and create_time <=
	#{endDate}

   select sum(sub_total) as income,DATE_FORMAT(create_time, '%y-%m-%d') as day from orders where deleted=0
	
		and seller_id =#{sellerId}
	
	
		and create_time >=
		#{beginDate}
	
	
		and create_time <=
		#{endDate}
	
    group by DATE_FORMAT(create_time, '%y-%m-%d') 
    order by DATE_FORMAT(create_time, '%y-%m-%d')

select count(*) from orders where deleted=0

	and uid=#{uid}

and state=#{state}
